import React, { useRef, useEffect} from 'react';
import WebViewer from '@pdftron/webviewer';
import './App.css';




const App = (props) => {
  const viewer = useRef(null);


  ///////////////////////////////////////////////////////////////////////////
    useEffect(() => {
      WebViewer(
        {
          path: '/webviewer/lib',
          initialDoc: props.pdfUrl,
        },  
        viewer.current,
      ).then((instance) => {
        instance.setTheme('dark');
        const { docViewer, Annotations } = instance;
        const annotManager = docViewer.getAnnotationManager();
        annotManager.importAnnotations(xfdfData);

        // get all annotation and form data
        // you might call this when you want to pass the XFDF data to your server to be saved
        // or you may want to pass an XFDF string to doc.getFileData to embed the annotations
        // in the downloaded PDF
        annotManager.exportAnnotations().then(xfdfData => { });



         ///////////////////////////////////////////////////
         ///save button header
         instance.setHeaderItems(header => {
          header.push({
            type: 'actionButton',
            img: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M0 0h24v24H0z" fill="none"/><path d="M17 3H5c-1.11 0-2 .9-2 2v14c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V7l-4-4zm-5 16c-1.66 0-3-1.34-3-3s1.34-3 3-3 3 1.34 3 3-1.34 3-3 3zm3-10H5V5h10v4z"/></svg>',
            onClick: async() => {
              console.log(await annotManager.exportAnnotations({ links: false, widgets: false }));
              // save the annotations
            }
          });
        });



         //////////////////////////


        docViewer.on('documentLoaded',  () => {
          const rectangleAnnot = new Annotations.RectangleAnnotation();
          rectangleAnnot.PageNumber = 1;
          // values are in page coordinates with (0, 0) in the top left
          rectangleAnnot.X = 50;
          rectangleAnnot.Y = 50;
          rectangleAnnot.Width = 100;
          rectangleAnnot.Height = 50;
          rectangleAnnot.Author = annotManager.getCurrentUser();


          annotManager.addAnnotation(rectangleAnnot);
          // need to draw the annotation otherwise it won't show up until the page is refreshed
          annotManager.redrawAnnotation(rectangleAnnot);


        });
      });
    }, []);
  // }


