import React, { useRef, useEffect} from 'react';
import WebViewer from '@pdftron/webviewer';
import './App.css';
import { storage } from './firebase';




const App = (props) => {
  const viewer = useRef(null);


  ///////////////////////////////////////////////////////////////////////////
    useEffect(() => {

      WebViewer(
        {
          path: '/webviewer/lib',
          initialDoc: 'https://www.eurofound.europa.eu/sites/default/files/ef_publication/field_ef_document/ef1710en.pdf',
        },  
        viewer.current,
      ).then((instance) => {
        instance.setTheme('light');
        const { docViewer, Annotations ,   CoreControls} = instance;
        const annotManager = docViewer.getAnnotationManager();
        /////////////////////////////////////////





          // Add header button that will get file data on click
    instance.setHeaderItems(header => {
      header.push({
          type: 'actionButton',
          img: '<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/></svg>',
          title: 'download',
          onClick: async () => {
            const xfdfString = await annotManager.exportAnnotations();
            const saveOptions = CoreControls.SaveOptions;
            const options = {
              filename: 'myDocument.pdf',
              xfdfString,
              flags: saveOptions.LINEARIZED,
              downloadType: 'pdf'
            };

            instance.downloadPdf(options);
          }
      });
    });

        ////////////////////////////////////////

        // instance.disableElements(['toolbarGroup-Shapes']);
        // instance.disableElements(['toolbarGroup-Edit']);
        // instance.disableElements(['toolbarGroup-Annotate']);
        // instance.disableElements(['toolbarGroup-Search']);

        // instance.disableElements(['toggleNotesButton']);
        // instance.disableElements(['searchButton']);
        // instance.disableElements(['selectToolButton']);
        // instance.disableElements(['textSelectButton']);
        // instance.disableElements(['panToolButton']);


        // instance.disableElements(['zoomOverlay']);

        // instance.disableElements(['viewControlsButton']);

        // instance.disableElements(['leftPanelButton']);
        // instance.disableElements(['zoomInButton']);
        // instance.disableElements(['zoomOutButton']);
        // instance.disableElements(['zoomOverlayButton']);
        // instance.disableElements(['zoomOverlayButton']);
        // instance.disableElements(['zoomOverlayButton']);
        // instance.disableElements(['rubberStampToolGroupButton']);
        // instance.disableElements(['stampToolGroupButton']);
        // instance.disableElements(['fileAttachmentToolGroupButton']);
        // instance.disableElements(['calloutToolGroupButton']);
        // instance.disableElements(['calloutToolGroupButton']);
        // instance.disableElements(['undoButton']);
        // instance.disableElements(['redoButton']);
        // instance.disableElements(['eraserToolButton']);
        // /////
        // instance.disableElements(['fullscreenButton']);
        // instance.disableElements(['printButton']);
        // instance.disableElements(['themeChangeButton']);
        // instance.disableElements(['eraserToolButton']);
        // instance.disableElements(['menuButton']);
        // instance.disableElements(['toolsOverlay']);
        instance.disableElements(['toolbarGroup-Shapes,toolbarGroup-Edit,toolbarGroup-Annotate']);








        ////////////////////////////////

        instance.setToolbarGroup('printButton');

        // set the default toolbar group to the Shapes group
        instance.setToolbarGroup('downloadButton');

         ///save button header
         instance.setHeaderItems(header => {
          header.push({

            type: 'actionButton',
            img: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M0 0h24v24H0z" fill="none"/><path d="M17 3H5c-1.11 0-2 .9-2 2v14c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V7l-4-4zm-5 16c-1.66 0-3-1.34-3-3s1.34-3 3-3 3 1.34 3 3-1.34 3-3 3zm3-10H5V5h10v4z"/></svg>',
            title: 'save button',
            onClick: async() => {
              console.log(await annotManager.exportAnnotations({ links: false, widgets: false }));
              // save the annotations
              //////////////
              const doc = docViewer.getDocument();
            const xfdfString = await annotManager.exportAnnotations();

            const data = await doc.getFileData({ xfdfString});
            const arr = new Uint8Array(data);
            const blob = new Blob([arr], { type: 'application/pdf' });
            let fileRef = storage.ref().child(props.pdfUrl);
            fileRef.put(blob);


            }
          });
        });



         //////////////////////////


        // docViewer.on('documentLoaded',async  () => {
        //   const doc = docViewer.getDocument();


        //   const rectangleAnnot = new Annotations.RectangleAnnotation();
        //   rectangleAnnot.PageNumber = 1;
        //   // values are in page coordinates with (0, 0) in the top left
        //   rectangleAnnot.X = 50;
        //   rectangleAnnot.Y = 50;
        //   rectangleAnnot.Width = 100;
        //   rectangleAnnot.Height = 50;
        //   rectangleAnnot.Author = annotManager.getCurrentUser();


        //   annotManager.addAnnotation(rectangleAnnot);
        //   // need to draw the annotation otherwise it won't show up until the page is refreshed
        //   annotManager.redrawAnnotation(rectangleAnnot);


        // });
      });
    }, []);


  return (
    <div className="App">
      {/* <div className="header">Rea</div> */}

      <div className="webviewer" ref={viewer}></div>
    </div>
  );
};

export default App;
