import ReactDOM from 'react-dom';
import './index.css';

import React, { useRef, useEffect, useState } from 'react';
 import WebViewer from '@pdftron/webviewer';
 import './App.css';



import {storage} from "./firebase"; 



////////////////////////////////////////////////////////////////////////////////

/////////////////////////////////////////////////////////////////////////////////////

const ReactFirebaseFileUpload =()=>{
  const [image,setImage] = useState(null);
  const [url,setUrl]=useState("");
  const [progress,setProgress] = useState(0);

const handleChange = e =>{
  if(e.target.files[0]){
    setImage(e.target.files[0]);

  }
};

const handleUpload = ()=>{
  const uploadTask = storage.ref(`images/${image.name}`).put(image);
  uploadTask.on(
    "state_changed",
    snapshot =>{
      const progress = Math.round((snapshot.bytesTransferred/snapshot.totalBytes)*100);
      setProgress(progress);
    },
    error=> {
      console.log(error);
    },
    ()=>{
      storage
      .ref("images")
      .child(image.name)
      .getDownloadURL()
      .then(url =>{
        setUrl(url);
      });
    }
    );
};

const openpdf=(url)=>{
  const viewer = useRef(null);
  // const [pdfUrl, setPdfUrl] = useState(pdfUrl);

  // useEffect(()=>{
  //   setPdf(props.pdfUrl);
  // },[props.pdfUrl])

  // // if using a class, equivalent of componentDidMount 
  // if(pdf){
    useEffect(() => {
      WebViewer(
        {
          path: '/webviewer/lib',
          initialDoc: url,
        },
        viewer.current,
      ).then((instance) => {

        const { docViewer, Annotations } = instance;
        const annotManager = docViewer.getAnnotationManager();

        docViewer.on('documentLoaded', () => {
          const rectangleAnnot = new Annotations.RectangleAnnotation();
          rectangleAnnot.PageNumber = 1;
          // values are in page coordinates with (0, 0) in the top left
          rectangleAnnot.X = 50;
          rectangleAnnot.Y = 50;
          rectangleAnnot.Width = 100;
          rectangleAnnot.Height = 50;
          rectangleAnnot.Author = annotManager.getCurrentUser();

          annotManager.addAnnotation(rectangleAnnot);
          // need to draw the annotation otherwise it won't show up until the page is refreshed
          annotManager.redrawAnnotation(rectangleAnnot);
        });
      });
    }, []);  
}

console.log("image: ",image);

  return(
      <>
    <div>

    <progress value={progress} max="100"/>
    <br/>
    <br/>

      Hii all!
      <br/>

      <input type="file" onChange={handleChange}/>
      <button onClick={handleUpload}>upload</button>
      <button onClick={openpdf}>open</button>
      <br/>
      {url}
      <br/>
      <br/>
      <iframe src={url || "http://via.placeholder.com/300*400"} alt="firbase-file"/>
    </div>

   {/* // <App pdfUrl={url}/> */}

     <div className="App">
      <div className="header">React sample</div>

      <div className="webviewer" ref={viewer}></div>
    </div>
    </>
  );
};

ReactDOM.render(<><ReactFirebaseFileUpload/></>, document.getElementById('root'));
