// import React,{useState} from 'react'; 
import {storage} from "./firebase";
import ReactDOM from 'react-dom';
import './index.css';

import React, { useRef, useEffect} from 'react';
import WebViewer from '@pdftron/webviewer';

// import './App.css';


const PdfApp =()=>{

  const viewer = useRef(null);
  useEffect(() => {

    WebViewer(
      {
        path: '/webviewer/lib',
        initialDoc: 'https://www.eurofound.europa.eu/sites/default/files/ef_publication/field_ef_document/ef1710en.pdf',
      },  
      viewer.current,
    ).then((instance) => {
      instance.setTheme('light');
      const { docViewer, Annotations ,   CoreControls} = instance;
      const annotManager = docViewer.getAnnotationManager();


      // async showSignaturePanel() {
      //   if (viewer.current.querySelector('iframe').contentDocument)
      //     viewer.current.querySelector('iframe').contentDocument.querySelector(
      //       'div[data-element="signatureToolButton"]'
      //     ).firstChild.click();
      // }
      /////////////////////////////////////////

        // Added download button in header
  instance.setHeaderItems(header => {
    header.push({
        type: 'actionButton',
        img: '<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M19 9h-4V3H9v6H5l7 7 7-7zM5 18v2h14v-2H5z"/></svg>',
        title: 'download',
        onClick: async () => {
          const xfdfString = await annotManager.exportAnnotations();
          const saveOptions = CoreControls.SaveOptions;
          const options = {
            filename: 'myDocument.pdf',
            xfdfString,
            flags: saveOptions.LINEARIZED,
            downloadType: 'pdf'
          };

          instance.downloadPdf(options);
        }
    });
  });


    ///disabled unuseful elements in tool bar
instance.disableElements(['zoomInButton','zoomOutButton','zoomOverlayButton','startFormEditingToolGroupButton','cropToolGroupButton','printButton','eraserToolButton','themeChangeButton','fullscreenButton','eraserToolButton','redoButton','undoButton','calloutToolGroupButton','fileAttachmentToolGroupButton','fileAttachmentToolGroupButton','stampToolGroupButton','rubberStampToolGroupButton','leftPanelButton','viewControlsButton','zoomOverlay','panToolButton','textSelectButton','selectToolButton','searchButton','toggleNotesButton','toolbarGroup-Annotate','toolbarGroup-Shapes','toolbarGroup-Edit','toolbarGroup-Annotate','menuButton']);
   //added save button in header toolbar

       instance.setHeaderItems(header => {
        header.push({

          type: 'actionButton',
          img: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M0 0h24v24H0z" fill="none"/><path d="M17 3H5c-1.11 0-2 .9-2 2v14c0 1.1.89 2 2 2h14c1.1 0 2-.9 2-2V7l-4-4zm-5 16c-1.66 0-3-1.34-3-3s1.34-3 3-3 3 1.34 3 3-1.34 3-3 3zm3-10H5V5h10v4z"/></svg>',
          title: 'save button',
          onClick: async() => {
            console.log(await annotManager.exportAnnotations({ links: false, widgets: false }));
            // save the annotations
            //////////////
            const doc = docViewer.getDocument();
          const xfdfString = await annotManager.exportAnnotations();

          const data = await doc.getFileData({ xfdfString});
          const arr = new Uint8Array(data);
          const blob = new Blob([arr], { type: 'application/pdf' });
          let fileRef = storage.ref().child('https://www.eurofound.europa.eu/sites/default/files/ef_publication/field_ef_document/ef1710en.pdf' );
          fileRef.put(blob);


          }
        });
      });


    });
  }, []);


  return(
      <>
    <button class="MuiButtonBase-root MuiFab-root reallos-fab MuiFab-extended" tabindex="0" type="button"><span class="MuiFab-label"><span class="reallos-fab-icon" style="margin-right: 15px; height: 25px;"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 29.364 29.364" fill="var(--color-primary)"><path id="Path_487" data-name="Path 487" d="M23.858,14.682H20.041a1.688,1.688,0,0,1-1.688-1.688v-.543a9.745,9.745,0,0,1,1.231-4.458A5.436,5.436,0,0,0,20.1,4.528,5.539,5.539,0,0,0,15.671.086,5.8,5.8,0,0,0,14.682,0,5.506,5.506,0,0,0,9.176,5.506a5.391,5.391,0,0,0,.5,2.269,12.489,12.489,0,0,1,1.337,5.215v0a1.688,1.688,0,0,1-1.688,1.688H5.506A5.506,5.506,0,0,0,0,20.188v2.753a.917.917,0,0,0,.918.918h.918v3.671a1.835,1.835,0,0,0,1.835,1.835H25.694a1.835,1.835,0,0,0,1.835-1.835V23.858h.918a.917.917,0,0,0,.918-.918V20.188A5.506,5.506,0,0,0,23.858,14.682ZM2.753,20.188a2.756,2.756,0,0,1,2.753-2.753H9.323a4.447,4.447,0,0,0,4.441-4.445,14.961,14.961,0,0,0-1.591-6.37,2.637,2.637,0,0,1-.244-1.115,2.756,2.756,0,0,1,2.753-2.753A3.062,3.062,0,0,1,15.2,2.8,2.779,2.779,0,0,1,17.39,5a2.676,2.676,0,0,1-.252,1.727A12.341,12.341,0,0,0,15.6,12.451v.543a4.446,4.446,0,0,0,4.441,4.441h3.817a2.756,2.756,0,0,1,2.753,2.753v.918H2.753Zm22.023,6.423H4.588V23.858H24.776Z"></path></svg></span>E-Sign Document</span><span class="MuiTouchRipple-root"></span></button>
    <div className="App">

      <div className="webviewer" ref={viewer}></div>
    </div>



     </>
  );
};

ReactDOM.render(<><PdfApp/></>, document.getElementById('root'));
