import { Context } from './../../index';
import { AuthenticationError } from 'apollo-server';
import jwt from 'jsonwebtoken';
import { User } from '../../entities/User';
import { UserTypes } from '../../resolvers';
import { throwError } from '../../../../../shared/errors';

const addUser = async (p, args, context) => {
  const { input } = args;
  const user = await User.create(input, 'Mutation.addUser', context);
  return user.resolveAsEntity();
};

const endSession = async (p, args, context: Context) => {
  context.res.clearCookie(context.jwtCookieName);
  return true;
};

const loginAnonymous = async (p, args, context: Context) => {
  const { userName } = args;
  const { user, res, jwtCookieName } = context;

  let loggedInUser: User;
  // if the user already exists, just get it
  if (user) {
    const userId = user.id;
    loggedInUser = userId && (await User.buildFromId(userId, 'Mutation.loginAnonymous', context));
  }

  // if no user is found, make a new one
  if (!loggedInUser) {
    const newUser = await User.create(
      {
        type: UserTypes.Anonymous,
        userName
      },
      'Mutation.loginAnonymous',
      context
    );
    loggedInUser = newUser;
  }

  if (!process.env.COLLAB_KEY) {
    throwError(
      'Mutation.loginAnonymous',
      'Please provide a COLLAB_KEY environment variable to generate auth token.'
    );
  }

  const token = jwt.sign(
    {
      id: loggedInUser.id,
      userName
    },
    process.env.COLLAB_KEY
  );

  res.cookie(jwtCookieName, token, {
    httpOnly: true,
    maxAge: 1000 * 60 * 60 * 24 * 31
  });

  return {
    user: (await loggedInUser?.resolveAsEntity()) || null
  };
};
const login = async (p, args, context: Context) => {
  const { email, password, token } = args;
  const { user, verifyPassword, getUserFromToken, jwtCookieName, res } = context;
  let loggedInUser: User = null;
  if (token) {
    //token provided by user - token flow
    let userId;
    if (user) {
      userId = user.id;
    } else {
      const userFromToken = await getUserFromToken(token);
      userId = userFromToken?.id;
    }
    try {
      loggedInUser = userId && (await User.buildFromId(userId, 'Mutation.login', context));
    } catch (e) {
      // nothing
    }

    if (!loggedInUser) {
      res.clearCookie(jwtCookieName);
      throwError('Mutation.login', 'Authentication failed. Cannot find user.', AuthenticationError);
    }
    res.cookie(jwtCookieName, token, {
      httpOnly: true,
      maxAge: 1000 * 60 * 60 * 24 * 31
    });
  } else if (email && password) {
    // U/P flow
    let verified = true;
    if (verifyPassword) {
      verified = await verifyPassword(email, password);
    } else {
      throwError(
        'Mutation.login',
        'Please provided a verifyPassword function for authentication.',
        AuthenticationError
      );
    }
    if (!verified)
      throwError('Mutation.login', 'Authentication Failed. Wrong password.', AuthenticationError);
    loggedInUser = await User.buildFromEmail(email, 'Mutation.login', context);
    if (!loggedInUser) {
      throwError('Mutation.login', `User with email ${email} is not found.`);
    }
    if (!process.env.COLLAB_KEY)
      throwError(
        'Mutation.login',
        'Please provide a COLLAB_KEY environment variable to generate auth token.'
      );

    const token = jwt.sign(
      {
        id: loggedInUser.id,
        userName: await loggedInUser?.get('userName')
      },
      process.env.COLLAB_KEY
    );

    res.cookie(jwtCookieName, token, {
      httpOnly: true,
      maxAge: 1000 * 60 * 60 * 24 * 31
    });
  } else {
    throwError(
      'Mutation.login',
      'User must use email and password to log in.',
      AuthenticationError
    );
  }
  return {
    user: (await loggedInUser?.getEntity()) || null
  };
};

export default { addUser, loginAnonymous, login, endSession };
