const CULL_TIME = 1000 * 60 * 60 * 10; // 10 mins

type ScrollPosition = {
  scrollTop: number;
  scrollHeight: number;
};

type ScrollSyncValue = {
  userId: string;
  position: ScrollPosition;
};

class UserConnection {

  currentDocuments: DocumentConnection[] = [];
  currentPageMap: Map<string, number> = new Map<string, number>();

  constructor(
    public userId,
    public timeConnected = Date.now()
  ) { }

  connectedToDocument(doc: DocumentConnection) {
    const exists = !!this.currentDocuments.find(e => e.documentId === doc.documentId);
    if (!exists) {
      this.currentDocuments.push(doc); 
    }
  }

  setCurrentPage(docId, page: number) {
    this.currentPageMap.set(docId, page);
  }

  getCurrentPage(docId) {
    return this.currentPageMap.get(docId);
  }
}

class DocumentConnection {
  connectedUserArray: UserConnection[] = []
  connectedUserMap: Map<string, UserConnection> = new Map<string, UserConnection>();
  scrollInformation?: ScrollSyncValue;

  constructor(
    public documentId,
  ) {

  }

  isEmpty() {
    return this.connectedUserArray.length === 0;
  }

  userIsConnectedToDocument(userId: string) {
    return this.connectedUserMap.has(userId);
  }

  userConnected(user: UserConnection) {
    const existingArrayIndex = this.connectedUserArray.findIndex(e => e.userId === user.userId);
    if (existingArrayIndex === -1) {
      this.connectedUserArray.push(user);
    } else {
      this.connectedUserArray[existingArrayIndex] = user
    }    
    this.connectedUserMap.set(user.userId, user);
  }

  userDisconnected(userId: string) {
    this.connectedUserArray = this.connectedUserArray.filter(u => u.userId !== userId)
    this.connectedUserMap.delete(userId);
  }

  removeScrollInfo() {
    this.scrollInformation = null;
  }
}

export default class ConnectedUserMap {
  userConnectionMap = new Map<string, UserConnection>();
  documentConnectionMap = new Map<string, DocumentConnection>()

  constructor() {
    setInterval(this.cull, CULL_TIME);
  }

  getConnectedUsersForDocument(documentId: string) {
    return this.documentConnectionMap.get(documentId)?.connectedUserArray || [];
  }

  getUserCurrentPage(userId: string, documentId: string) {
    return this.userConnectionMap.get(userId)?.getCurrentPage(documentId) || null;
  }

  getScrollPositionForDocument(documentId: string) {
    return this.documentConnectionMap.get(documentId)?.scrollInformation || null;
  }

  setCurrentPageForUser(userId: string, documentId: string, currentPage: number) {
    this.connectUserToDocument(userId, documentId);
    this.userConnectionMap.get(userId)?.setCurrentPage(documentId, currentPage);
  }

  setScrollPositionForDocument(userId, documentId: string, position: ScrollPosition) {
    this.connectUserToDocument(userId, documentId);
    this.documentConnectionMap.get(documentId).scrollInformation = {
      userId,
      position
    }
  }

  connectUserToDocument(userId: string, documentId: string, pageNumber?: number) {
    let connectedUser = this.userConnectionMap.get(userId)
    if (!connectedUser) {
      connectedUser = new UserConnection(userId);
      this.userConnectionMap.set(userId, connectedUser);
    }

    let connectedDocument = this.documentConnectionMap.get(documentId);
    if (!connectedDocument) {
      connectedDocument = new DocumentConnection(documentId)
      this.documentConnectionMap.set(documentId, connectedDocument);
    }

    connectedUser.connectedToDocument(connectedDocument)
    if (typeof pageNumber === 'number') {
      connectedUser.setCurrentPage(documentId, pageNumber);
    }

    connectedDocument.userConnected(connectedUser);
    return true;
  }


  disconnectUserFromDocument(userId: string, documentId: string) {
    const doc =  this.documentConnectionMap.get(documentId)
    doc?.userDisconnected(userId);
    if (doc?.isEmpty()) {
      this.documentConnectionMap.delete(documentId);
    }
    this.userConnectionMap.delete(userId);
    return true;
  }

  disconnectUserFromAllDocuments(userId) {
    const documents = this.userConnectionMap.get(userId)?.currentDocuments || [];
    return documents.map(doc => {
      this.disconnectUserFromDocument(userId, doc.documentId);
      if (doc.isEmpty()) {
        this.documentConnectionMap.delete(doc.documentId);
      }
      return doc.documentId
    })
  }

  disconnectFromScroll(documentId) {
    const doc = this.documentConnectionMap.get(documentId);
    if (!doc) return false;
    doc?.removeScrollInfo();
    return true;
  }

  leaveScroll(userId: string): string[] {
    const documents = this.userConnectionMap.get(userId)?.currentDocuments;
    if (!documents || documents.length === 0) return [];

    documents.reduce((acc, document) => {
      const scrollInfo = document.scrollInformation;
      if (!scrollInfo) return acc;

      const { userId } = scrollInfo;
      if (userId === userId) {
        document.removeScrollInfo();
        acc.push(document.documentId);
      }

      return acc;
    }, [])
  }

  connect(userId: string) {
    const newUser = new UserConnection(userId);
    this.userConnectionMap.set(userId, newUser);
  }

  /**
   * Checks if a user is connected or not
   * used to be .has()
   */
  isUserConnected(userId: string) {
    return this.userConnectionMap.has(userId);
  }

  /**
   * Used to be .delete()
   */
  disconnectUser(userId: string) {
    const userConnection = this.userConnectionMap.get(userId);
    const documents = userConnection?.currentDocuments || [];
    documents.forEach(doc => {
      doc.userDisconnected(userId);
      if (doc.isEmpty()) {
        this.documentConnectionMap.delete(doc.documentId);
      }
    })
    this.userConnectionMap.delete(userId);
  }

  /**
   * Used to be .keys()
   */
  getAllConnectedUsers() {
    return this.userConnectionMap.keys();
  }

  clear() {
    this.userConnectionMap.clear();
    this.documentConnectionMap.clear();
  }

  hasDocumentForScroll(documentId: string) {
    const doc = this.documentConnectionMap.get(documentId);
    return !!doc?.scrollInformation;
  }

  cull() {
    const data = this.userConnectionMap.entries();
    const now = Date.now();
    for (const [userId, userConnection] of data) {
      if (now - userConnection.timeConnected >= CULL_TIME) {
        this.disconnectUser(userId);
      }
    }
  }
}
