import logger from '../../../../../shared/log/logger';
import { Mention } from '../../entities/Mention';

const addMention = async (p, args, context) => {
  const { input } = args;
  const { annotationId, userId } = input;
  const existingMention = await Mention.buildFromId(annotationId, userId, 'Mutation.addMention', context);
  if(existingMention) {
    logger.warn('Mention already exists.');
    return existingMention.resolveAsEntity();
  }
  const mention = await Mention.create(input, 'Mutation.addMention', context);

  await mention.publishEvent(context.pubsub, 'ADD');

  return mention.resolveAsEntity();
};

const editMention = async (p, args, context) => {
  const { userId, annotationId, input } = args;
  const mention = await Mention.buildFromId(annotationId, userId, 'Mutation.editMention', context);
  await mention.setMultiple(input);
  await mention.save();
  return mention.resolveAsEntity();
};

const deleteMention = async (p, args, context) => {
  const { userId, annotationId } = args;
  const mention = await Mention.buildFromId(annotationId, userId, 'Mutation.deleteMention', context);
  if(!mention) {
    const message = 'Mutation.deleteMention: Mention not found.'
    logger.warn(message);
    return {
      successful: false,
      message
    }
  }
  const deleteResult = await mention.delete();
  await mention.publishEvent(context.pubsub, 'DELETE');

  return deleteResult;
};

export default { addMention, editMention, deleteMention };
