import { joinFrom } from './../util/joinFrom';
import { ApolloDatabaseMention, SubscriptionTypes } from './../resolvers';
import { User } from './User';
import { createEntity } from './../entity';
import { Context } from '../resolvers';
import { Mention as ApolloMention, NewMentionInput } from './../../../../types/resolvers-types';
import { Base } from './Base';
import logger, { Tags } from '../../../../shared/log/logger';
import { PubSub } from 'apollo-server';

export class Mention extends Base<
ApolloDatabaseMention,
ApolloMention,
  {
    mentionedUser: User;
  }
> {
  constructor(mention: ApolloDatabaseMention, from: string, context: Context) {
    super({
      name: 'Mention',
      entity: mention,
      from,
      context,
      remove: async (id) => await context.resolvers.Mutation.deleteMention(id),
      getters: {
        mentionedUser: async () => {
          const u = await context.resolvers.Query.user(mention.userId);
          const user = new User(u, joinFrom(from, 'getters.mentionedUser'), context);
          return user;
        }
      },
      update: async (entity) => await context.resolvers.Mutation.editMention(entity.id, entity),
    });
  }

  async publishEvent(pubsub: PubSub, action: 'ADD' | 'DELETE') {
    //property name in subscriptionFields should be identical with the operation name in packages/collab-client/src/graphql/subscriptions/mentionChanged.ts
    const mentionChangedRequestedFields = this.context.subscriptionFields['OnMentionChanged'];
    const { userId } = this.getEntity();
    const { connectedUsers } = this.context;
    if(!connectedUsers.isUserConnected(userId)) return;
    const subscriptionContext = {...this.context, requestedFields: mentionChangedRequestedFields};
    const mention = await (new Mention(this.getEntity(), 'Mention.publishEvent', subscriptionContext)).resolveAsEntity();

    pubsub.publish(SubscriptionTypes.MENTION_CHANGED, {
      mentionChanged: {
        mention,
        action
      },
      userId
    })

  }

  static async buildFromId(annotationId: string, userId: string, from: string, context: Context) {
    const mention = (await context.resolvers.Query.mentions({ annotationId, userId }))?.[0];
    if(!mention) return null;
    const mentionObject = new Mention(mention, joinFrom(from, `Mention.buildFromId(${annotationId}, ${userId})`), context);
    return mentionObject;
  }

  /**
   * Creates a new mention and saves it in the database
   */
  static async create(input: NewMentionInput, from: string, context: Context): Promise<Mention> {
    const newEntity = await createEntity(input);
    logger.debug(`New Mention Entity created:`, { tag: Tags.ENTITY, meta: newEntity });
    //check user, annotation existence??
    const mention = new Mention(newEntity, joinFrom(from, 'Mention.create'), context);
    await context.resolvers.Mutation.addMention(newEntity);

    return mention;
  }
}
