import { Email, MessageEmailData, MessageData, InviteEmailData, CollabServerEmailOptions, EmailFunction } from '..';
export class EmailManager {
  disabled: boolean;
  emailHandler: EmailFunction;
  messageEmailQueue: Array<MessageData>;
  EMAIL_QUEUE_TIMER: number;
  timer: NodeJS.Timeout;
  constructor(emailOptions: CollabServerEmailOptions) {
    this.disabled = !emailOptions || !emailOptions.emailHandler;
    if (!this.disabled) {
      this.emailHandler = emailOptions.emailHandler;
      this.messageEmailQueue = [];
      this.EMAIL_QUEUE_TIMER = emailOptions.emailQueueTimer || (1000 * 60 * 10); // default to 10 min
      this.timer = null;
    }
  }
  addToMessageEmailQueue(data: MessageData) {
    if (this.disabled) return;
    // setup timer if no message in the queue
    if (this.messageEmailQueue.length === 0) {
      this.timer = setTimeout(() => {
        // remove duplicate messages
        this.messageEmailQueue = this.uniqueArray(this.messageEmailQueue);
        this.triggerMessageEmail(this.messageEmailQueue);
      }, this.EMAIL_QUEUE_TIMER);
    }
    this.messageEmailQueue.push(data);
  }
  deleteFromMessageEmailQueue(annotationId: string) {
    if (this.disabled) return;
    this.messageEmailQueue = this.messageEmailQueue.filter((message) => message.annotationId !== annotationId);
  }

  updateMessageTextInEmailQueue(annotationId: string, messageText: string) {
    if (this.disabled) return;
    const index = this.messageEmailQueue.findIndex(
      (message) => message.annotationId === annotationId
    );
    if (index !== -1) {
      const updatedData = { ...this.messageEmailQueue[index], messageText };
      this.messageEmailQueue[index] = updatedData;
    }
  }

  resetMessageEmailQueue() {
    this.messageEmailQueue = [];
  }
  async triggerMessageEmail(data: Array<MessageData>) {
    const type = Email.Message;

    const mappedByEmail = data.reduce((acc, item) => {
      const { sendTo } = item;
      sendTo.forEach(email => {
        if (!acc[email]) acc[email] = [];
        acc[email].push(item);
      })
      return acc;
    }, {} as Record<string, MessageData[]>);

    const emailPromises = Object.keys(mappedByEmail).map(async email => {
      const messageData: MessageEmailData = {
        sendTo: email,
        messages: mappedByEmail[email]
      }

      await this.emailHandler(type, messageData);
    })

    await Promise.all(emailPromises);

    this.resetMessageEmailQueue();
  }
  triggerInviteEmail(data: InviteEmailData) {
    if (this.disabled) return;
    const type = Email.Invite;
    this.emailHandler(type, data);
  }
  uniqueArray<T>(arrayOfObjects: Array<T>): Array<T> {
    return arrayOfObjects.filter((object, index) => index === arrayOfObjects.findIndex((obj) => JSON.stringify(obj) === JSON.stringify(object)));
  }
}
