import { Email, InviteEmailData, MessageEmailData, EmailFunction } from '..';
import type sgMail from '@sendgrid/mail';
import logger from '../../../../shared/log/logger';
import { throwError } from '../../../../shared/errors';

type sendMailFunction = (type: Email, templateData: Record<string, any>, sendTo: string | string[]) => Promise<void>;

type AsyncOrSync<T> = Promise<T> | T;
type TemplateData = {
  subject?: string,
} & Record<any, any>

export type EmailServiceOptions = {
  /**
   * A reference to '@sendgrid/mail'
   */
  sendGrid: typeof sgMail;
  /**
   * Your sendgrid API key
   */
  emailServiceApiKey: string;
  /**
   * The email address or domain you verified from your email service
   */
  emailSender: string;
  /**
   * The name of the sender
   */
  senderName?: string;
  /**
   * Sendgrid template Ids for each email type
   */
  templateId?: {
    message: string;
    invite: string;
  };

  getTemplateData?: (type: Email, data: InviteEmailData | MessageEmailData) => AsyncOrSync<TemplateData>
};

const sendGridEmailHandler = (emailServiceOptions: EmailServiceOptions): EmailFunction => {
  const sgMail = emailServiceOptions.sendGrid;

  if (!sgMail) {
    throwError('EmailManager.emailHandler', 'A reference to the sendGrid SDK (@sendgrid/mail) is a required.');
  }

  const sendMail: sendMailFunction = async (type, templateData: TemplateData, sendTo) => {
    sgMail.setApiKey(emailServiceOptions.emailServiceApiKey);
    const msg: sgMail.MailDataRequired = {
      to: sendTo,
      from: {
        email: emailServiceOptions.emailSender,
        name: emailServiceOptions.senderName || ''
      },
      subject: templateData.subject,
      templateId: emailServiceOptions.templateId[type],
      dynamicTemplateData: templateData
    };

    try {
      sgMail.send(msg)
    } catch (error) {
      logger.error(`An error occured while sending an email - ${error.message}`);
      logger.debug(JSON.stringify(msg, null, 2));
    }
  };

  return async (type, data) => {
    const { getTemplateData } = emailServiceOptions;

    if (type === Email.Invite) {
      const inviteData = data as InviteEmailData;
      const { sendTo, documentName, documentId, sentBy } = inviteData;
      const templateData = getTemplateData ? await getTemplateData(type, inviteData) : {};

      const finalTemplateData = {
        documentName,
        documentId,
        sentBy,
        ...templateData
      }

      await sendMail(type, finalTemplateData, sendTo);
    }

    if (type === Email.Message) {
      const messageData = data as MessageEmailData;
      const templateData = getTemplateData ? await getTemplateData(type, messageData) : {};
      const { sendTo, messages } = messageData;

      const finalTemplateData = {
        messages,
        ...templateData
      }

      await sendMail(type, finalTemplateData, sendTo);
    }
  };
};

export default sendGridEmailHandler;
